import React, {useState} from 'react';
import './App.css';
import Preview from './component/Preview';

function App() {

  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [selectedNote, setSelectedNode] = useState(null);
  const [creating, setCreating] = useState(false);
  const [editing, setEditing] = useState(false);

  // Add Note Handler
  const addNoteHandler = () => (
    setCreating(true)
  );

  // Change Title Value Note
  const changeTitleHandler = (event) => {
    setTitle(event.target.value)
  }

  // Change Content Value Note
  const changeContentHandler = (event) => {
    setContent(event.target.value)
  }

  // Save Note Handler
  const saveNoteHandler = () => {
    const note = {
      id: new Date(),
      title: title,
      content: content
    }

    const updateNote = [...notes, note];

    setNotes(updateNote);
    setCreating(false);
    setSelectedNode(note.id);

    setTitle('');
    setContent('')
  }

  const getAddNote = () => {

    return (
      <div>
        <h2>إضافة ملاحظة جديدة</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
            onChange={changeContentHandler}
          />

          <a href="#" className="button green" onClick={saveNoteHandler}>
            حفظ
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {
    if(notes.length === 0) {
      return (
        <h2 className='center'>No Note Found</h2>
      )
    }

    if(!selectedNote) {
      return (
        <h2 className='center'>Please Select Note</h2>
      )
    }

    const note = notes.find(note => {
      return note.id === selectedNote;
    })
    return (
      <div>
        <div className="note-operations">
          <a href="#">
            <i className="fa fa-pencil-alt" />
          </a>
          <a href="#">
            <i className="fa fa-trash" />
          </a>
        </div>
        <div>
          <h2>{note.title}</h2>
          <p>{note.content}</p>
        </div>
      </div>
    );
  };

  return (
    <div className="App">
      <div className="notes-section">
        <ul className="notes-list">
          <li className="note-item">ملاحظة رقم #1</li>
          <li className="note-item">ملاحظة رقم #2</li>
          <li className="note-item">ملاحظة رقم #3</li>
          <li className="note-item">ملاحظة رقم #4</li>
        </ul>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </div>
      <div className="preview-section">{creating ? getAddNote() : getPreview()}</div>
    </div>
  );
}

export default App;
