import React, {useState} from 'react';
import './App.css';

function App() {

  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [creating, setCreating] = useState(null);
  const [selectedNote, setSelectedNode] = useState([]);

  // Add Note Handler
  const addNoteHandler = () => (
    setCreating(true)
  );


    // Change Title Value Note
    const changeTitleValue = (event) => {
      setTitle(event.target.value)
    }

    // Change Content Value Note
    const changeContentValue = (event) => {
      setContent(event.target.value)
    }

  const getAddNote = () => {

    const note = {
      id: new Date(),
      title: title,
      content: content
    }

    return (
      <div>
        <h2>إضافة ملاحظة جديدة</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleValue}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
            onChange={changeContentValue}
          />

          <a href="#" className="button green">
            حفظ
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {
    return (
      <div>
        <div className="note-operations">
          <a href="#">
            <i className="fa fa-pencil-alt" />
          </a>
          <a href="#">
            <i className="fa fa-trash" />
          </a>
        </div>
        <div>
          <h2>عنوان ملاحظة تجريبية</h2>
          <p>نص ملاحظة تجريبية</p>
        </div>
      </div>
    );
  };

  return (
    <div className="App">
      <div className="notes-section">
        <ul className="notes-list">
          <li className="note-item">ملاحظة رقم #1</li>
          <li className="note-item">ملاحظة رقم #2</li>
          <li className="note-item">ملاحظة رقم #3</li>
          <li className="note-item">ملاحظة رقم #4</li>
        </ul>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </div>
      <div className="preview-section">{creating ? getAddNote() : getPreview()}</div>
    </div>
  );
}

export default App;
