import React, {useState} from 'react';
import './App.css';
import Preview from './component/Preview';
import Message from './component/Message';
import NoteContainer from './component/Notes/NoteContainer';
import NoteList from './component/Notes/NoteList';
import Note from './component/Notes/Note';

function App() {

  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [selectedNote, setSelectedNote] = useState(null);
  const [creating, setCreating] = useState(false);
  const [editing, setEditing] = useState(false);

  // Add Note Handler
  const addNoteHandler = () => (
    setCreating(true)
  );

  // Change Title Value Note
  const changeTitleHandler = (event) => {
    setTitle(event.target.value)
  }

  // Change Content Value Note
  const changeContentHandler = (event) => {
    setContent(event.target.value)
  }

  // Save Note Handler
  const saveNoteHandler = () => {
    const note = {
      id: new Date(),
      title: title,
      content: content
    }

    const updateNote = [...notes, note];

    setNotes(updateNote);
    setCreating(false);
    setSelectedNote(note.id);
    setTitle('');
    setContent('')
  }

  // Select Note Handler
  const selectNoteHandler = (noteId) => {
    return setSelectedNote(noteId);
  }

  // Edit Note Handler
  const editNoteHandler = () => {
    const note = editedNotes.find(note => {
      note.id === selectedNote;
    })
    setEditing(true);
    setTitle(note.title);
    setContent(note.content);
  }

  const getAddNote = () => {

    return (
      <div>
        <h2>إضافة ملاحظة جديدة</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
            onChange={changeContentHandler}
          />

          <a href="#" className="button green" onClick={saveNoteHandler}>
            حفظ
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {
    if(notes.length === 0) {
      return (
        <Message title='No Note Found' />
      )
    }

    if(!selectedNote) {
      return (
        <Message title='Please Select Note' />
      )
    }

    const note = notes.find(note => {
      return note.id === selectedNote;
    })
    return (
      <div>
        {!editing && 
        <div className="note-operations">
          <a href="#" onClick={editNoteHandler}>
            <i className="fa fa-pencil-alt" />
          </a>
          <a href="#">
            <i className="fa fa-trash" />
          </a>
        </div>
        }

        <div>
          <h2>{note.title}</h2>
          <p>{note.content}</p>
        </div>
      </div>
    );
  };

  return (
    <div className="App">
      <NoteContainer>
        <NoteList>
          {notes.map(note => <Note 
                                key={note.id}
                                title={note.title}
                                noteClicked={() => selectNoteHandler(note.id)}
                                active={selectedNote === note.id}
                              />
          )}
        </NoteList>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </NoteContainer>
      <Preview>
        {creating ? getAddNote() : getPreview()}
      </Preview>
    </div>
  );
}

export default App;
